package StringFuncs;

public class Split {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 String a="Nikhila";
		 String b="Sen";
		 String c="Nijan";
		 String d="Love to learn";
		 String e= "     Love  to learn";
		 String f="";
		 
		 System.out.println(a.length());
		 System.out.println(b.toUpperCase());
		 System.out.println(c.toLowerCase());
		 System.out.println(d.charAt(4));
		 System.out.println(a.indexOf('h'));
		 System.out.println(d.substring(4));
		 System.out.println(d.substring(4,9));
		 System.out.println(e.trim());
		 System.out.println(a.contains("f"));
		 System.out.println(a.isEmpty());
		 System.out.println(a.matches(b));
		 System.out.println(a.concat(b));
		 System.out.println(a.concat("").concat(c));












		 
		 
		 

	}

}
