package coreJavaToken;

public class constructorDatatypes {
	
	constructorDatatypes(float a)
	{
		System.out.println("Float Datatype");
	}
	constructorDatatypes(double a)
	{
		System.out.println("Double Datatype");
	}
	constructorDatatypes(char a)
	{
		System.out.println("Char Datatype");
	}
	constructorDatatypes(String b)
	{
		System.out.println("String Datatype");
	}
	constructorDatatypes(int c)
	{
		System.out.println("Integer Datatype");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 new constructorDatatypes(11.2f);
 new constructorDatatypes(11.21);
 new constructorDatatypes('N');
 new constructorDatatypes("Niki");
 new constructorDatatypes(11);

	}

}
